import axios, { AxiosError, InternalAxiosRequestConfig, AxiosResponse } from 'axios';
import { User, LoginFormData, ApiResponse } from '../types';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// Helper function to handle API errors
const handleApiError = (error: any, defaultMessage: string) => {
  console.error('API Error:', error);
  return {
    success: false,
    message: error.response?.data?.message || error.message || defaultMessage,
  };
};

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true, // Important for cookies/sessions
});

// Add auth token to requests if it exists
api.interceptors.request.use((config: InternalAxiosRequestConfig) => {
  const token = localStorage.getItem('accessToken');
  if (token) {
    config.headers = config.headers || {};
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Handle 401 responses
api.interceptors.response.use(
  (response: AxiosResponse) => response,
  (error: AxiosError) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('accessToken');
      if (!window.location.pathname.includes('/login')) {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

const authService = {
  async login(credentials: LoginFormData): Promise<ApiResponse<{ user: User; accessToken: string }>> {
    try {
      console.log('ðŸ”‘ Attempting login with credentials:', credentials);
      const response = await api.post('/auth/login', credentials);
      console.log('âœ… Login response:', response.data);

      if (response.data.success && response.data.data?.accessToken) {
        localStorage.setItem('accessToken', response.data.data.accessToken);
      }
      return response.data;
    } catch (error: any) {
      return handleApiError(error, 'Login failed. Please check your credentials and try again.');
    }
  },

  async logout(): Promise<{ success: boolean; message?: string }> {
    try {
      await api.post('/auth/logout');
      localStorage.removeItem('accessToken');
      return { success: true };
    } catch (error: any) {
      return handleApiError(error, 'Logout failed');
    }
  },

  async refreshToken(): Promise<ApiResponse<{ accessToken: string }>> {
    try {
      const response = await api.post('/auth/refresh-token');
      if (response.data.success && response.data.data?.accessToken) {
        localStorage.setItem('accessToken', response.data.data.accessToken);
      }
      return response.data;
    } catch (error: any) {
      localStorage.removeItem('accessToken');
      throw error;
    }
  },

  getCurrentUser(): User | null {
    const token = localStorage.getItem('accessToken');
    if (!token) return null;

    // In a real app, you would decode the JWT or make an API call to get user info
    // For now, return a minimal user object
    return {
      id: 0,
      username: 'user',
      email: 'user@example.com'
    };
  },

  isAuthenticated(): boolean {
    return !!localStorage.getItem('accessToken');
  }
};

const weatherService = {
  async getWeatherData(city: string = 'London'): Promise<ApiResponse<any>> {
    try {
      const response = await api.get(`/weather?city=${encodeURIComponent(city)}`);
      return response.data;
    } catch (error: any) {
      return handleApiError(error, 'Failed to fetch weather data');
    }
  },
};

// Export the authService as a named export
export { authService };

// Export the api instance as default
export default api;
