import axios, { AxiosError, AxiosRequestConfig } from 'axios';
import { User, LoginFormData, WeatherData, ApiResponse } from '../types';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const api = axios.create({
  baseURL: API_BASE_URL,
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json',
  },
});

let isRefreshing = false;
let failedQueue: Array<{ resolve: (token: string) => void; reject: (error: any) => void }> = [];

const processQueue = (error: any = null, token: string | null = null) => {
  failedQueue.forEach(prom => {
    if (error) {
      prom.reject(error);
    } else {
      prom.resolve(token!);
    }
  });
  failedQueue = [];
};

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('accessToken');
  const csrfToken = document.querySelector('meta[name="csrf-token"]')?.getAttribute('content');

  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }

  if (csrfToken) {
    config.headers['X-CSRF-Token'] = csrfToken;
  }

  return config;
});

// Move authService definition before interceptors that use it
const authService = {
  async login(credentials: LoginFormData): Promise<ApiResponse<User>> {
    try {
      console.log('üîë Attempting login with credentials:', credentials);

      // Log the full request details
      console.log('üì§ Request details:', {
        url: `${api.defaults.baseURL}/auth/login`,
        method: 'POST',
        data: credentials,
        headers: {
          'Content-Type': 'application/json',
          ...(api.defaults.headers.common as any)
        }
      });

      const response = await api.post('/auth/login', credentials);

      console.log('‚úÖ Login response:', {
        status: response.status,
        statusText: response.statusText,
        headers: response.headers,
        data: response.data
      });

      if (response.data.success && response.data.data?.accessToken) {
        console.log('üîê Login successful, storing access token');
        localStorage.setItem('accessToken', response.data.data.accessToken);

        // Make sure user data is properly set in the response
        if (!response.data.data.user) {
          console.warn('‚ö†Ô∏è Login successful but no user data in response');
          response.data.data = { 
            ...response.data.data, 
            user: { 
              id: 0, 
              username: credentials.username, 
              email: `${credentials.username}@example.com` 
            } 
          };
        }
      } else {
        console.warn('‚ùå Login response indicates failure:', response.data);
      }

      return response.data;
    } catch (error: any) {
      console.error('Login error:', error);
      const errorMessage = error.response?.data?.message || 
                         error.message || 
                         'Login failed. Please check your network connection and try again.';
      return {
        success: false,
        message: errorMessage,
      };
    }
  },

  async logout(): Promise<ApiResponse<null>> {
    try {
      const response = await api.post('/auth/logout');
      localStorage.removeItem('accessToken');
      return response.data;
    } catch (error: any) {
      return {
        success: false,
        message: error.response?.data?.message || 'Logout failed',
      };
    }
  },

  async refreshToken(): Promise<ApiResponse<{ accessToken: string }>> {
    try {
      const response = await api.post('/auth/refresh-token');
      if (response.data.success && response.data.data?.accessToken) {
        localStorage.setItem('accessToken', response.data.data.accessToken);
      }
      return response.data;
    } catch (error: any) {
      localStorage.removeItem('accessToken');
      throw error;
    }
  },
};

api.interceptors.response.use(
  (response) => response,
  async (error: AxiosError) => {
    const originalRequest = error.config as (AxiosRequestConfig & { _retry?: boolean }) | undefined;

    // If there's no response or the request config is missing, reject
    if (!error.response || !originalRequest) {
      return Promise.reject(error);
    }

    const isAuthRequest = originalRequest.url?.includes('/auth/');
    const isLoginPage = window.location.pathname === '/login';

    // Handle 401 Unauthorized errors
    if (error.response.status === 401) {
      // If we're already on the login page or it's an auth request, reject
      if (isLoginPage || isAuthRequest) {
        return Promise.reject(error);
      }

      // If we're already refreshing the token, add the request to the queue
      if (isRefreshing) {
        return new Promise((resolve, reject) => {
          failedQueue.push({ 
            resolve: (token: string) => {
              if (originalRequest.headers) {
                originalRequest.headers.Authorization = `Bearer ${token}`;
              }
              resolve(api(originalRequest));
            }, 
            reject 
          });
        });
      }

      // Mark that we're refreshing the token
      isRefreshing = true;

      try {
        const response = await authService.refreshToken();
        const { accessToken } = response.data || {};

        if (accessToken) {
          // Update the original request with the new token
          if (originalRequest.headers) {
            originalRequest.headers.Authorization = `Bearer ${accessToken}`;
          }

          // Process any queued requests with the new token
          processQueue(null, accessToken);

          // Retry the original request
          return api(originalRequest);
        } else {
          // If no access token in response, redirect to login
          if (!isLoginPage) {
            window.location.href = '/login';
          }
          return Promise.reject(new Error('No access token in refresh response'));
        }
      } catch (refreshError) {
        // Clear any stored tokens and redirect to login
        localStorage.removeItem('accessToken');
        if (!isLoginPage) {
          window.location.href = '/login';
        }
        processQueue(refreshError, '');
        return Promise.reject(refreshError);
      } finally {
        isRefreshing = false;
      }
    }

    // For other errors, just reject
    return Promise.reject(error);
  }
);

// Export the authService that was defined earlier
export { authService };

export const weatherService = {
  async getWeatherData(city: string = 'London'): Promise<ApiResponse<WeatherData>> {
    try {
      const response = await api.get(`/weather?city=${encodeURIComponent(city)}`);
      return response.data;
    } catch (error: any) {
      return {
        success: false,
        message: error.response?.data?.message || 'Failed to fetch weather data',
      };
    }
  },
};

export default api;
